mapper.select:
SELECT A.*, B.JOB_GRADE_NM AS MANAGER_JG_NM FROM ( SELECT TVRM.*, TUM.USER_NM AS MANAGER_NM, TUM.JOB_GRADE FROM ( SELECT REQ_ID, CRE_ID, STORE_NM, ADDRESS, DATE_FORMAT(CRE_DTM, '%Y-%m-%d') AS STRING_CRE_DT, DATE_FORMAT(CONTRACT_DT,'%Y-%m-%d') AS STRING_CONTRACT_DT, SHOOT_SCHEDULE, PHONE, IS_URGENT_REQ, NOTE, PROGRESS_NOTE, MANAGER_ID, STATUS, DATE_FORMAT(SHOOT_RESERVE_DTM, '%Y-%m-%d %H:%i') AS STRING_SHOOT_RESERVE_DTM, DATE_FORMAT(SHOOT_COMPLETE_DT,'%Y-%m-%d') AS STRING_SHOOT_COMPLETE_DT, DATE_FORMAT(UPLOAD_COMPLETE_DT,'%Y-%m-%d') AS STRING_UPLOAD_COMPLETE_DT FROM TBV_VIDEO_REQ_M) TVRM LEFT JOIN TBU_USER_M TUM ON TVRM.MANAGER_ID = TUM.USER_ID ) A LEFT JOIN TBG_GRADE_C B ON A.JOB_GRADE = B.CODE
STORE_NM LIKE CONCAT('%', #{searchStoreNm}, '%')
ORDER BY CASE STATUS WHEN 'COORDINATION' THEN 1 WHEN 'STANDBY' THEN 2 WHEN 'COMPLETEFILM' THEN 3 WHEN 'COMPLETEUPLOAD' THEN 4 ELSE 5 END, CASE WHEN IS_URGENT_REQ = 'Y' THEN 1 ELSE 2 END, REQ_ID DESC LIMIT #{startRow}, #{pageSize};
